function decoder3(data) {
    console.log(data);
    const bytes = Buffer.from(data, 'hex');
    const decoded = {};
    let i = 0;

    while (i < bytes.length) {
        const channel_id = bytes[i++];

        if (channel_id === 0x70) {
            decoded.soilMoisture = bytes[i];
            i += 1;
        } else if (channel_id === 0x67) {
            decoded.soilTemperature = bytes[i];
            i += 1;
        } else if (channel_id === 0xC0) {
            decoded.soilEC = bytes[i];
            i += 1;
        } else if (channel_id === 0xC2) {
            decoded.soilPH = bytes[i];
            i += 1;
        } else {
            break;
        }
    }

    return decoded;
}

global.set('decoder3', decoder3);

